package program;

public class DataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//--implicit type---
		char a='a';
		System.out.println("Value: "+a);
		
		int b='b';
		
		System.out.println("Value: "+b);	

		
		//----explicit type---
		double num=45.5;
		
	int num1=(int) num;
	System.out.println("Value: "+num1);
	}

}
